import React, { useState } from 'react';
import DataTable from '../components/DataTable';
import Axios from "axios";
export const columns = [
    {
      Header: "Receiver",
      accessor: "name",
    },
    {
      Header: "Weight",
      accessor: "weight",
    },
    {
      Header: "Box Colour",
      accessor: "color",
      Cell: row => {
        row.styles['backgroundColor'] = row.value;
        return "";
      }
    },
    {
      Header: "Shipping cost",
      accessor: "cost",
    }
  ];


  export const cost = {
    "Sweden": 1.3,
    "China": 4,
    "Brazil": 8.6,
    "Australia": 7.4
  };

  const getBoxes = () => {
    Axios.get("http://localhost:30010/employees").then((response) => {
      let list = [];
      response.data.array.forEach(element => {
        let map = {
          id: element.id,
          name: element.name,
          weight: element.weight,
          color: "green",
          cost: element.weight * cost[element.country],
        };
        list.push(map);
      });
      return list;
    });
  };
  export const data = getBoxes();

export class ListBoxes extends React.Component {
    render() {
        return (
            <DataTable 
            columns = {columns}
            data = {data}
            />
        )
    }
}
export default ListBoxes;
