import React from 'react'
import { GithubPicker, PhotoshopPicker } from 'react-color'
import "./componentsStyles.css"

class ButtonExample extends React.Component {
  state = {
    displayColorPicker: false,
    background: "#ffff"
  };

  handleChangeComplete = (color) => {
    this.setState({ background: color.hex });
    this.setState({ displayColorPicker: !this.state.displayColorPicker })
  };
  handleClick = () => {
    this.setState({ displayColorPicker: !this.state.displayColorPicker })
  };

  handleClose = () => {
    this.setState({ displayColorPicker: false })
  };

  render() {
    const popover = {
      position: 'absolute',
      zIndex: '2',
    }
    const cover = {
      position: 'fixed',
      top: '0px',
      right: '0px',
      bottom: '0px',
      left: '0px',
    }
    const colors = [
      '#B80000', '#DB3E00', '#FCCB00', 
      '#008B02', '#006B76', '#1273DE', 
      '#004DCF', '#5300EB', '#EB9694', 
      '#FAD0C3', '#FEF3BD', '#C1E1C5', 
      '#BEDADC', '#C4DEF6', '#BED3F3', '#D4C4FB'];
    return (
      <div>
        <button className="colorButton"

        onClick={ this.handleClick }>Pick Color</button>
        { this.state.displayColorPicker ? <div style={ popover }>
          <div style={ cover } onClick={ this.handleClose }/>
          <GithubPicker className="colorPicker"
          colors={colors}
          color={ this.state.background }
          onChangeComplete={this.handleChangeComplete}
          />
        </div> : null }
      </div>
    )
  }
}

export default ButtonExample
