import React, { useState } from 'react';
import DataTable from '../components/DataTable';
import Axios from "axios";
export const setListBoxes = useState([]);
export const columns = [
    {
      Header: "Receiver",
      accessor: "receiver",
    },
    {
      Header: "Weight",
      accessor: "weight",
    },
    {
      Header: "Box Colour",
      accessor: "color",
      Cell: row => {
        row.styles['backgroundColor'] = row.value;
        return "";
      }
    },
    {
      Header: "Shipping cost",
      accessor: "cost",
    }
  ];


  export const cost = {
    "Sweden": 1.3,
    "China": 4,
    "Brazil": 8.6,
    "Australia": 7.4
  };

  export const getBoxes = () => {
    Axios.get("http://localhost:3001/getboxes").then((response) => {
      let data = response.data;
      let list = [];
      console.log("hello");
      data.forEach((element) => {
        let map = {
          id: element.id,
          receiver: element.name,
          weight: element.weight,
          color: "green",
          cost: element.weight * cost[element.country],
        };
        list.push(map);
      });
      setListBoxes(list);
      return list;
    });
  };
  export const data = getBoxes();

export class ListBoxes extends React.Component {
    render() {
        return (
            <div>
            {getBoxes().map((val, element) => {
              <div>
              <h3>Name: {val.name}</h3>
              <h3>weight: {val.weight}</h3>
              <h3>Cost: {val.cost}</h3>
              <h3>Color: {val.color}</h3>
              <h3>Id: {val.id}</h3>
            </div>
            })}
            </div>


        )
    }
}
export default ListBoxes;
