import React from 'react';
import Button from '../components/Button';
import ColorPicker from '../components/ColorPicker';
import DropDownButton from '../components/DropDownButton';
import TextInput from '../components/TextInput';
import Axios from "axios";
import "./AddBox.css"

export class AddBox extends React.Component {
    constructor(props) {

        super(props)
        this.state = {
            name: '',
            weight: 0,
            country: 'Sweden',
            color: "#fff"
        }

        this.handleChangeName = this.handleChangeName.bind(this);
        this.handleChangeWeight = this.handleChangeWeight.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.onChangeDropButton=this.onChangeDropButton.bind(this);
    }

    handleChangeName(event) {
        this.setState({ name: event.target.value });
    }

    handleChangeWeight(event) {
        this.setState({ weight: event.target.value });
    }

    onChangeDropButton(event) {
        this.setState({country: event.target.value});
    }
    handleChangeComplete = (color) => {
        this.setState({ background: color.hex });
        this.setState({ displayColorPicker: !this.state.displayColorPicker })
    };
    handleClick(event) {
        if (this.state.weight < 0)
            return alert("Negative values are not permitted")

        if(this.state.name.isEmpty || this.state.country == null)
        return alert("");

        return alert(ColorPicker.getColor());
        //var color = document.getElementById('colorPicker').color;
        /*Axios.post("http://localhost:3003/create", {
            name: this.state.name,
            weight: this.state.weight,
            country: this.state.country,
            color: "black",
        }).then(() => {
            console.log("sucess");
        });*/
    }

    render() {
        return (
            <div className="information">
                <TextInput labelTitle="Name"
                    type="text"
                    name="name"
                    min=""
                    onChange={this.handleChangeName}
                />
                <TextInput labelTitle="Weight"
                    type="number"
                    min="0"
                    name="weight"
                    onChange={this.handleChangeWeight}
                />
                <ColorPicker 
                onChangeComplete={this.props.handleChangeComplete}

                />
                <box color={this.props.background} />
                <DropDownButton
                    options={['Sweden', 'China', 'Brazil', 'Australia']}
                    value = {this.setState.country}
                    onSelect = {this.onChangeDropButton}
                />

                <Button
                    children="Save"
                    onClick={this.handleClick}
                    className="saveButton"
                />
            </div>
        )
    }
}
export default AddBox;
