import React, { useMemo, useState, useEffect } from "react";
import ReactTable from 'react-table-6';
import 'react-table-6/react-table.css'
import Axios from "axios";

function ListBoxes() {
  const [loadingData, setLoadingData] = useState(true);
  const columns = useMemo(() => [
    {
      Header: "Receiver",
      accessor: "receiver",
    },
    {
      Header: "Weight",
      accessor: "weight",
    },
    {
      Header: "Box Colour",
      accessor: "color",
      Cell: row => {
        row.styles['backgroundColor'] = row.value;
        return "";
      }
    },
    {
      Header: "Shipping cost",
      accessor: "cost",
    }
  ]);
  const cost = {
    "Sweden": 1.3,
    "China": 4,
    "Brazil": 8.6,
    "Australia": 7.4
  };

  const spainSymbol = {
    "Sweden": "SEK",
    "China": "CNY",
    "Brazil": "BRL",
    "Australia": "AUD"
  };
  const [data, setData] = useState([]);

  function componentFromStr(numStr, percent) {
    var num = Math.max(0, parseInt(numStr, 10));
    return percent ?
      Math.floor(255 * Math.min(100, num) / 100) : Math.min(255, num);
  }
  function rgbToHex(rgb) {
    var rgbRegex = /^rgb\(\s*(-?\d+)(%?)\s*,\s*(-?\d+)(%?)\s*,\s*(-?\d+)(%?)\s*\)$/;

    var result, r, g, b, hex = "";
    if ((result = rgbRegex.exec(rgb))) {
      r = componentFromStr(result[1], result[2]);
      g = componentFromStr(result[3], result[4]);
      b = componentFromStr(result[5], result[6]);

      hex = "#" + (0x1000000 + (r << 16) + (g << 8) + b).toString(16).slice(1);
      console.log(result[5]);
    }
    return hex;
  }

  useEffect(() => {
    async function getData() {
      await Axios
        .get("http://localhost:3001/getboxes")
        .then((response) => {
          var list = [];
          response.data.forEach((element) => {
            list.push(
              {
                id: element.id,
                receiver: element.name,
                weight: element.weight + " kilograms",
                color: rgbToHex(element.color),
                cost: element.weight * cost[element.country]
                  + " " + spainSymbol[element.country]
              }
            );
          })
          console.log(response.data);
          setData(list);
          setLoadingData(false);
        });
    }
    if (loadingData) {
      // if the result is not ready so you make the axios call
      getData();
    }
  }, []);



  return (
    <div className="App">
      {/* here you check if the state is loading otherwise if you wioll not call that you will get a blank page because the data is an empty array at the moment of mounting */}
      {loadingData ? (
        <p>Loading Please wait...</p>
      ) : (
        <ReactTable className="ReactTable"
          data={data}
          columns={columns}
        />
      )}
    </div>
  );
}

export default ListBoxes;
