import React from 'react'
import { GithubPicker, PhotoshopPicker } from 'react-color'
import "./componentsStyles.css"

class ColorPicker extends React.Component {
  state = {
    displayColorPicker: false,
    background: "#fff",
  };
  handleChangeComplete = (color, _) => {
    this.setState({ background: color.hex });
  };

  handleClickChooseColor = () => {
    this.setState({ displayColorPicker: !this.state.displayColorPicker })
  };

  handleClose = () => {
    this.setState({ displayColorPicker: false })
  };

  render() {
    const popover = {
      position: 'absolute',
      zIndex: '2',
    }
    const cover = {
      position: 'fixed',
      top: '0px',
      right: '0px',
      bottom: '0px',
      left: '0px',
    }
    const colors = [
      '#B80000', '#DB3E00', 
      '#FCCB00', '#008B02', 
      '#006B76', '#5300EB', 
      '#EB9694', '#FAD0C3', 
      '#FEF3BD', '#C1E1C5', 
      '#BEDADC', '#D4C4FB'];
    return (
      <form>
        <label for="name">Colour</label>
        <button className="colorButton"
          onClick={this.handleClickChooseColor}>Click to show colour picker</button>
        {this.state.displayColorPicker ? <div style={popover}>
          <div style={cover} onClick={this.handleClose} />
          <PhotoshopPicker
            className="colorPicker"
            colors={colors}
            color={this.state.background}
            onSwatchHover={this.handleChangeComplete} />
        </div> : null}
      </form>
    )
  }
}

export default ColorPicker
