import DataTable from '../components/DataTable';
import Axios from "axios";

function ListBoxes() {

  const columns = ["Receiver", "Weight", "Box Colour", "Shipping cost"];
  /*{
    Header: "Receiver",
    accessor: "receiver",
  },
  {
    Header: "Weight",
    accessor: "weight",
  },
  {
    Header: "Box Colour",
    accessor: "color",
    Cell: row => {
      row.styles['backgroundColor'] = row.value;
      return "";
    }
  },
  {
    Header: "Shipping cost",
    accessor: "cost",
  }
];*/



  function getBoxes (){
    const cost = {
      "Sweden": 1.3,
      "China": 4,
      "Brazil": 8.6,
      "Australia": 7.4
    };
    var table = document.getElementById("boxesTable");
    Axios.get("http://localhost:3001/getboxes").then((response) => {
      response.data.map((element) => {
        var row  = `<tr> 
                        <th>${element.name}</th>
                        <th>${element.weight}</th>
                        <th>black</th>
                        <th>${element.weight* cost(element.country)}</th>
                    </tr>`
        table.innerHTML +=row;
      });
    });
  };
  getBoxes();
  return (
    <table>
      <tr>
        <th>Receiver</th>
        <th>Weight</th>
        <th>Box Colour</th>
        <th>Shipping cost</th>
      </tr>


      <tbody id="boxesTable"/>
    </table>
  );
}

export default ListBoxes;
