const popover = {
  position: 'absolute',
  zIndex: '2',
}
const cover = {
  position: 'fixed',
  top: '0px',
  right: '0px',
  bottom: '0px',
  left: '0px',
}
const colors = [
  '#B80000', '#DB3E00', '#FCCB00',
  '#008B02', '#006B76', '#5300EB',
  '#EB9694', '#FAD0C3', '#FEF3BD',
  '#C1E1C5', '#BEDADC', '#D4C4FB'];
const ColorPicker = ({onChangeComplete, colorButtonClick, displayColorPicker, color}) => (
  <div>
    <button className="colorButton"
      onClick={colorButtonClick}>Pick Color</button>
    {displayColorPicker ? <div style={popover}>

      <GithubPicker className="colorPicker"
        colors={colors}
        color={ color}
        onChangeComplete={onChangeComplete}
      />
    </div> : null}
  </div>
);

export default ColorPicker
