import DataTable from '../components/DataTable';
import Axios from "axios";

function ListBoxes() {

  const columns = [
    {
    Header: "Receiver",
    accessor: "receiver",
  },
  {
    Header: "Weight",
    accessor: "weight",
  },
  {
    Header: "Box Colour",
    accessor: "color",
    Cell: row => {
      row.styles['backgroundColor'] = row.value;
      return "";
    }
  },
  {
    Header: "Shipping cost",
    accessor: "cost",
  }
];

const cost = {
  "Sweden": 1.3,
  "China": 4,
  "Brazil": 8.6,
  "Australia": 7.4
};

const spainSymbol = {
  "Sweden": "SEK",
  "China": "CNY",
  "Brazil": "BRL",
  "Australia": "AUD"
};

const data  = Axios.get("http://localhost:3001/getboxes").then((response) => {
  return response.data;
});

  function getBoxes (){

    var table = document.getElementById("boxesTable");
    Axios.get("http://localhost:3001/getboxes").then((response) => {
      response.data.map((element) => {
        var row  = `<tr>
                        <th>${element.name}</th>
                        <th>${element.weight}</th>
                        <th></th>
                        <th>${element.weight* cost[element.country]}</th>
                    </tr>`
        table.innerHTML +=row;
      });
    });
  };
  getBoxes();
  return (
    <DataTable 
    columns = {columns}
    data={data}
    />
  );
}

